Bash(1 <<'TINKER_EOF'
echo "=== تتبع مصدر المدن في Checkout Shipping ===\\n\\n";

$merchantId = 2;

// 1. Customer City - from session \(saved in Step 1: Address\)
echo "1. مدينة العميل \(من Session\):\\n";
$addressKey = "checkout.merchant.{$merchantId}.address";
$addressData = Session::get\($addressKey\);

if \($addressData\) {
    echo "   customer_city: " . \($addressData['customer_city'] ?? 'N/A'\) . "\\n";
    echo "   city_id: " . \($addressData['city_id'] ?? 'N/A'\) . "\\n";
    echo "   latitude: " . \($addressData['latitude'] ?? 'N/A'\) . "\\n";
    echo "   longitude: " . \($addressData['longitude'] ?? 'N/A'\) . "\\n";
} else {
    echo "   ❌ لا توجد بيانات في Session\\n";
}

// 2. Merchant City - from ShippingCalculatorService
echo "\\n2. مدينة التاجر \(من قاعدة البيانات\):\\n";
$merchantCityData = \\App\\Services\\ShippingCalculatorService::getMerchantCity\($merchantId\);
print_r\($merchantCityData\);

// 3. Show merchant's city_id in users table
echo "\\n3. بيانات التاجر في جدول users:\\n";
$merchant = DB::table\('users'\)->where\('id', $merchantId\)->first\(['id', 'name', 'shop_name', 'city', 'city_id']\);
if \($merchant\) {
    echo "   id: {$merchant->id}\\n";
    echo "   shop_name: {$merchant->shop_name}\\n";
    echo "   city: {$merchant->city}\\n";
    echo "   city_id: {$merchant->city_id}\\n";
}
TINKER_EOF)

